<template>
  <div>
    <header>
      <img @click="back()" src="data:image/png;base64," alt="">
      <h2>选择所在省份</h2>
    </header>
    <div class="location">
      当前定位：
    </div>
    <div class="getcity">
      <div>
        <img src="data:image/png;base64," alt="">
        点击定位当前位置
      </div>
    </div>
    <div class="select">
      <span>请选择</span>省份/城市
    </div>
    <div class="content">
      <div class="province" v-for="data in datalist" :key="data.province_id" @click="city(data.province_id)">{{data.province_name}}</div>
    </div>
    <router-view></router-view>
  </div>
</template>

<script>
import Axios from 'axios'
export default {
  data () {
    return {
      datalist: []
    }
  },
  mounted () {
    this.$store.state.isprovince = false
    Axios.post('/lct?api_version=2.3.0&platType=2&client=wap&isEncry=0&time=1576917366401&act=Address&op=get_region', 'region=province_name').then(res => {
      this.datalist = res.data.datas
      // console.log(res.data.datas)
    })
  },
  methods: {
    city (id) {
      this.$router.push('/city')
      this.$store.state.provinceid = id
      // console.log(this.$store.state.provinceid)
    },
    back () {
      this.$router.push('/lecun')
    }
  },
  beforeDestroy () {
    this.$store.state.isprovince = true
  }
}
</script>

